#include <stdio.h>
#include <gmp.h>


const char* n_str =
"";

const char* g_str =
"";

const char* e_str =
"";

int main()
{
  mpz_t n;
  mpz_t g;
  mpz_t e;
  mpz_t p;

  mpz_init(n);
  mpz_init(g);
  mpz_init(e);
  mpz_init(p);

  mpz_set_str(n, n_str, 10);
  mpz_set_str(g, g_str, 10);
  mpz_set_str(e, e_str, 10);

  mpz_powm(p, g, e, n);

  printf("n =\n%s\n\n", mpz_get_str(0, 10, n));
  printf("g =\n%s\n\n", mpz_get_str(0, 10, g));
  printf("e =\n%s\n\n", mpz_get_str(0, 10, e));
  printf("p =\n%s\n\n", mpz_get_str(0, 10, p));
}
